                        <Register 
                            username={username}
                            setUsername={setUsername}
                            password={password}
                            setPassword={setPassword}
                            userToken={userToken}
                            setUserToken={setUserToken}
                            setLoggedIn={setLoggedIn}
                            loggedIn={loggedIn}
                            // history
                        />
                    </Route>
                    <Route exact path="/merchandise">
                        <ProductPage
                            merchandise={merchandise}
                            setMerchandise={setMerchandise}
                        />
                    </Route>
